<template>
  <div class="content">
    <div id="animation" class="animation paused play">222</div>
    <button @click="handup">举手</button>
    <button @click="paused">暂停动画</button>
    <button @click="play">播放动画</button>
    <img v-if="showGif" src="../../assets/images/1.gif" alt="">
    <img v-if="showGif" src="../../assets/images/1.gif" alt="">
    <button @click="showGif = !showGif">显示</button>
  </div>
</template>

<script>
export default {
  name: 'SetpAnimation',
  data() {
    return {
      showGif: false
    }
  },
  methods: {
    handup () {
      const el = document.querySelector('#animation')
      el.setAttribute('class', 'aaa')
      setTimeout(() => {
        el.setAttribute('class', 'animation aaa')
      }, 0);
    },
    paused () {
      const el = document.querySelector('#animation')
      el.classList.remove('play')
      setTimeout(() => {
        el.classList.add('paused')
      }, 0);
    },
    play () {
      const el = document.querySelector('#animation')
      el.classList.remove('paused')
      setTimeout(() => {
        el.classList.add('play')
      }, 0);
    },
  }
}
</script>

<style scoped>
.aaa {
  width: 116px;
  height: 116px;
}
.animation {
  width: 116px;
  height: 116px;
  max-height: 100%;
  background-repeat: no-repeat;
  background-position: 0% 0;
  animation: handup 1s steps(20), handup2 1s steps(13) 19 1s;
}
.play {
  animation-play-state: running;
}
.paused {
  animation-play-state: paused;
}
@keyframes handup {
  0% {
    background-position: 0% 0%;
    background-image: url(data:image/png;base64,);
  }
  100% {
      background-position: 100% 0%;
      background-image: url(data:image/png;base64,);
  }
}

@keyframes handup2 {
  0% {
    background-position: 0% 0%;
    background-image: url(data:image/png;base64,);
  }
  100% {
      background-position: 100% 0%;
      background-image: url(data:image/png;base64,);
  }
}
</style>
